    onStartBtnClick (e) {
        this.savedData.completion = 100;
        this.savedData.status = CourseConstants.STATUS_COMPLETE;
        CourseActions.saveData({
            name: this.props.guid,
            value: this.savedData
        });
        if (this.props.navigateNext) {
            this.props.navigateNext();
        }
    },
	render () {
	    var menuGuid = this.props.menuGuid && this.props.menuGuid != ''
	        ? this.props.menuGuid
	        : 'menu';
	    var menu = (
	        <ComponentLoader 
    	        {...this.props}
    	        className="menu"
    	        guid={menuGuid}
    	        navigateToPage={this.props.navigateToPage}
    	        template={null} />
    	);

	    return (
	        <div className="content-wrapper">
	            <div 
	                className="bg-image" 
	                style={{
	                    backgroundImage: `url('${this.props.backgroundImage}')`
	                }} />
	            {menu}
	            <div className="intro-content">
	                <h1>{this.props.header}</h1>
	                <div 
	                    className="introduction" 
	                    dangerouslySetInnerHTML={{
	                        __html: this.props.introduction
	                    }} />
	                <button onClick={this.onStartBtnClick}>{this.props.buttonLabel}</button>
	            </div>
	        </div>
	    );
	}
});

