.DefaultUI {
    $blue: #99ccee;
    $orange: #ff9922;

    height: 100%;
    width: 100%;

    .ui-container {
        $menu-btn-height: 50px;
        $offset-left: 25px;

        height: 100%;
        width: 100%;

        .menu-btn {
            background: none;
            background-color: $blue;
            border: none;
            border-radius: 0;
            cursor: pointer;
            height: $menu-btn-height;
            left: $offset-left;
            margin: 0;
            outline: none;
            padding: 15px;
            position: absolute;
            top: 0;
            z-index: 3;

            &.selected {
                background-color: #fff;

                .burger-layer {
                    background-color: $blue;
                }
            }

            .burger-layer {
                background-color: #fff;
                display: block;
                height: 3px;
                margin-bottom: 3px;
                width: 20px;
            }
        }

        .menu-container {
            background-color: rgba(#000, 0.5);
            height: 100%;
            left: 0;
            position: absolute;
            text-align: center;
            top: 0;
            width: 100%;
            z-index: 1;

            &.fade-enter, &.fade-appear {
                opacity: 0;

                &.fade-enter-active, &.fade-appear-active {
                    opacity: 1;

                    -webkit-transition: opacity 1000ms ease-in-out;
                    -moz-transition: opacity 1000ms ease-in-out;
                    -ms-transition: opacity 1000ms ease-in-out;
                    -o-transition: opacity 1000ms ease-in-out;
                    transition: opacity 1000ms ease-in-out;
                }
            }

            &.fade-leave {
                opacity: 1;

                &.fade-leave-active {
                    opacity: 0;

                    -webkit-transition: opacity 1000ms ease-in-out;
                    -moz-transition: opacity 1000ms ease-in-out;
                    -ms-transition: opacity 1000ms ease-in-out;
                    -o-transition: opacity 1000ms ease-in-out;
                    transition: opacity 1000ms ease-in-out;
                }
            }

            .menu {
                background-color: #fff;
                height: calc(100% - #{$offset-left + $menu-btn-height});
                left: $offset-left;
                position: absolute;
                top: $menu-btn-height;
                vertical-align: middle;
                width: calc(100% - #{$offset-left * 2});
                z-index: 2;

                .menu-close {
                    $font-size: 30px;

                    background: none;
                    border: none;
                    color: #555;
                    cursor: pointer;
                    font-size: $font-size;
                    font-weight: bold;
                    height: 25px;
                    line-height: $font-size;
                    margin: 0;
                    margin-top: -($font-size / 5);
                    outline: none;
                    padding: 0;
                    position: absolute;
                    right: 10px;
                    top: 10px;
                    width: 25px;
                }
            }

            @media (max-width: 600px) {
                .menu {
                    width: calc(100% - 3px);

                    .menu-close {
                        right: 0;
                        top: 0;
                    }
                }
            }
        }

        .page-container {
            background-color: #fff;
            height: 100%;
            left: 0;
            position: absolute;
            top: 0;
            width: 100%;

            &.fade-appear, &.fade-enter {
                opacity: 0;

                &.fade-appear-active, &.fade-enter-active {
                    opacity: 1;

                    -webkit-transition: opacity 1000ms ease-in-out;
                    transition: opacity 1000ms ease-in-out;
                }
            }

            &.fade-leave {
                opacity: 1;

                &.fade-leave-active {
                    opacity: 0;

                    -webkit-transition: opacity 1000ms ease-in-out;
                    transition: opacity 1000ms ease-in-out;
                }
            }
        }
    }
}

