.TextMultipleChoice {
    .content-wrapper {
        padding: 20px;
        padding-top: 40px;

        .feedback {
            margin-top: 15px;
        }

        .intro-container {
            margin-bottom: 20px;

            .directions, .stem, h1 {
                margin-bottom: 10px;
            }
        }

        .options {
            .option {
                cursor: pointer;
                margin-bottom: 10px;

                &.disabled {
                    cursor: default;
                }

                &.selected {
                    .status {
                        background-color: #ff9922;
                    }
                }

                .label {

                }

                .label, .status {
                    display: inline-block;
                    vertical-align: top;
                }

                .status {
                    background-color: #fff;
                    border: 2px solid #ff9922;
                    height: 15px;
                    margin-right: 10px;
                    width: 15px;

                    &.radio {
                        border-radius: 50%;
                    }
                }
            }
        }
    }
}

